            {isWelfare && (
              <div className="space-y-6">
                <h3 className="text-lg font-medium border-b pb-2">公益咨询资格证明</h3>

                <FormField
                  control={form.control}
                  name="welfareProofFile"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>上传证明材料 *</FormLabel>
                      <FormDescription>
                        请上传学生证、低保证明或其他经济困难相关证明（支持 JPG、PNG、GIF、PDF，最大10MB）
